mapper.select:
SELECT no, coffee, kind, price, DATE_FORMAT(reg_day,'%Y.%m.%d') AS reg_day, DATE_FORMAT(mod_day,'%Y.%m.%d') AS mod_day FROM coffee_menu WHERE 1=1 AND reg_day >= DATE_FORMAT( #{startDate},'%Y%m%d') AND reg_day < DATE_ADD(DATE_FORMAT(#{endDate},'%Y%m%d'), INTERVAL +1 DAY)
AND coffee like CONCAT(#{coffee},'%')
AND kind = #{kind}

mapper.update:
Update coffee_menu set
price = CAST(#{price} as INTEGER)


        #{item}
